using SpreadsheetLight;
using System;
using System.IO;

namespace SpreadsheetLightEjemplo2
{
    class Program
    {
        static void Main(string[] args)
        {


            LecturaXlsxEnBase64();

        }

        public static void LecturaXlsxEnRuta()
        {
            var path = AppDomain.CurrentDomain.BaseDirectory + "hola.xlsx";

            using (var sp = new SLDocument(path))
            {
                var wsn = sp.GetWorksheetStatistics();

                for (int i = 1; i <= wsn.EndRowIndex; i++)
                {
                    Console.WriteLine(sp.GetCellValueAsString(i, 1));
                }


            }
        }

        public static void LecturaXlsxEnBase64()
        {

            var archivoBase64 = new MemoryStream(Convert.FromBase64String(@""));

            using (var sp = new SLDocument(archivoBase64))
            {
                var wsn = sp.GetWorksheetStatistics();

                for (int i = 1; i <= wsn.EndRowIndex; i++)
                {
                    Console.WriteLine(sp.GetCellValueAsString(i, 1));
                }


            }
        }
    }
}
